async function cadastrarUsuario(){let a=$("#cadastrarUsuario").serialize(),s=$("#btn-cadastrar-usuario");$.ajax({type:"POST",url:"api/usuario",data:a,dataType:"json",beforeSend:function(){s.addClass("disabled"),s.html('<div class="spinner-border text-light" role="status"><span class="sr-only">Loading...</span></div> <span>Aguarde</span>')},success:function(a){a.success?toastr.success("Você será redirecionado para o login",a.message,{timeOut:3e3,fadeOut:3e3,onHidden:function(){$(location).delay(2e3).attr("href","login")}}):(toastr.error(a.message),s.removeClass("disabled"),s.html('<i class="fas fa-user-plus"></i> Cadastrar'))},error:function(a){toastr.error(a)}})}$(document).ready(function(){$("#cadastrarUsuario").submit(function(a){cadastrarUsuario(),a.preventDefault()})});