function login(){let e=$("#login").serialize(),s=$("#btn-login");$.ajax({type:"POST",url:"./api/login",data:e,dataType:"json",beforeSend:function(){s.addClass("disabled"),s.html('<div class="spinner-border text-light" role="status"><span class="sr-only">Loading...</span></div> <span>Entrando</span>')},success:function(e){e.success?toastr.success("Aguarde...",e.message,{timeOut:3e3,fadeOut:3e3,onHidden:function(){$(location).delay(1e3).attr("href","./home")}}):(toastr.error("Verifique seu email e senha"),s.removeClass("disabled"),s.html('<i class="fas fa-sign-in-alt"></i> Login'))},error:function(e){toastr.error("Verifique seu email e senha"),s.removeClass("disabled"),s.html('<i class="fas fa-sign-in-alt"></i> Login')}})}function logout(){let e=$("#btn-login");$.ajax({type:"DELETE",url:"api/login",dataType:"json",beforeSend:function(){e.addClass("disabled")},success:function(s){s.success?$(location).delay(1e3).attr("href","login"):(toastr.error(s.message),e.removeClass("disabled"))},error:function(s){toastr.error(s),e.removeClass("disabled")}})}$(document).ready(function(){$("#login").submit(function(e){login(),e.preventDefault()})});